import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Home from "./Home";
import PageNotFound from "./PageNotFound";
import Order from "./Order";
import Orders from "./Orders";
import MyOrders from "./MyOrders";
import Modal from "./Modal";
import { useState } from "react";
import { removeDeal, saveDeal } from "./fetchCalls";
import Ordered from "./Ordered";

function App() {
  const orders = [
    {
      id: 0,
      title: "Burger 'n Fries",
      restaurant: "Dulce",
      price: "$6.99",
      description:
        "angus beef, American cheese, fried onions, and sriracha mayo all sandwiched between two sesame seed buns.",
      notes: "",
    },
    {
      id: 1,
      title: "Blueberry Acai",
      restaurant: "Sunlife Organics",
      price: "$4.99",
      description:
        "one large banana sliced, one cup of blueberries, almondmilk, all in a standard acai bowl.",
      notes: "",
    },
    {
      id: 2,
      title: "Collard Greens",
      restaurant: "Honeybird",
      price: "$1.99",
      description:
        "classic collard greens cooked low and slow, just like mama made 'em.",
      notes: "",
    },
    {
      id: 3,
      title: "Avocado Toast",
      restaurant: "Greenleaf Chopshop",
      price: "$4.99",
      description:
        "one California avocado smashed, a poached egg, and sea salt all on whole-wheat toast.",
      notes: "",
    },
    {
      id: 4,
      title: "Turkey Sandwich",
      restaurant: "Nature's Brew",
      price: "$4.99",
      description:
        "free-range turkey breast, mayo, sliced avocado, lettuce, and tomato between two pieces of sourdough bread.",
      notes: "",
    },
    {
      id: 5,
      title: "Mediterranean Mezze",
      restaurant: "Cava",
      price: "$3.99",
      description:
        "a variety of appetizers from the Mediterranean, including stuffed olives, stuffed grape leaves, and hummus and whole-wheat pita.",
      notes: "",
    },
  ];
  const [myOrders, setMyOrders] = useState([]);

  function deleteOrder(orderToBeDeleted) {
    removeDeal(orderToBeDeleted.id).then(() => {
      const filteredOrders = orders.filter((order) => {
        return order.id !== orderToBeDeleted.id;
      });

      setMyOrders(filteredOrders);
    });
  }

  function editOrder(orderToBeEdited, newTitle, newLabelId) {
    saveDeal({
      id: orderToBeEdited.id,
      title: newTitle,
      label: newLabelId,
    }).then((updatedOrder) => {
      const updatedOrders = orders.map((order) => {
        if (order.id === updatedOrder.id) {
          return updatedOrder;
        } else {
          return order;
        }
      });

      setMyOrders(updatedOrders);
    });
  }

  function addOrder(title, labelId) {
    saveDeal({
      title: title,
      label: labelId,
    }).then((newOrder) => {
      setMyOrders(orders.concat(newOrder));
    });
  }
  return (
    <Router>
      <Switch>
        <Route path="/" exact={true}>
          <Home />
          <Modal />
        </Route>
        <Route path="/orders" exact={true}>
          <Orders orders={orders} />
        </Route>
        <Route path="/order/:id" exact={true}>
          <Order orders={orders} addOrder={addOrder} />
        </Route>
        <Route path="/ordered/:id" exact={true}>
          <Ordered
            orders={orders}
            editOrder={editOrder}
            deleteOrder={deleteOrder}
          />
        </Route>
        <Route path="/myorders" exact={true}>
          <MyOrders />
        </Route>
        <Route path="*">
          <PageNotFound />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
