import React from "react";
import { useParams } from "react-router-dom";
import logo from "./pictures/pomegranate.svg";
import { Formik, Field, Form } from "formik";
import { saveDeal, removeDeal } from "./fetchCalls";

export default function Ordered({ orders, editOrder, deleteOrder }) {
  const sleep = (ms) => new Promise((r) => setTimeout(r, ms));
  const { id } = useParams();
  const order = orders.find((orderr) => {
    return orderr.id === Number(id);
  });
  return (
    <>
      <title>Order {id}</title>
      <nav
        className="navbar navbar-expand-lg navbar-dark fixed-top"
        id="mainNav"
        style={{ backgroundColor: "black" }}
      >
        <div className="container">
          <a className="navbar-brand js-scroll-trigger" href="/">
            <img
              src={logo}
              alt="AirBLD Logo"
              className="navbar-brand js-scroll-trigger"
              href="#page-top"
              id="logo"
              width="15%"
            />
          </a>
          <button
            className="navbar-toggler navbar-toggler-right"
            type="button"
            data-toggle="collapse"
            data-target="#navbarResponsive"
            aria-controls="navbarResponsive"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            Menu
            <i className="fas fa-bars" />
          </button>
          <div className="collapse navbar-collapse" id="navbarResponsive">
            <ul className="navbar-nav text-uppercase ml-auto">
              <li className="nav-item">
                <a className="nav-link js-scroll-trigger" href="/orders">
                  See Deals
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link js-scroll-trigger" href="/myorders">
                  My Orders
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <span></span>
      <img
        id="food"
        src={order.src}
        alt=""
        style={{
          position: "relative",
          top: "300px",
          left: "50px",
        }}
      />
      <div
        className="mr-5"
        style={{
          position: "relative",
          left: "50%",
        }}
      >
        <h3>
          Dish: <i>{order.title}</i>
        </h3>
        <h3>
          Restaurant: <i>{order.restaurant}</i>
        </h3>
        <h3>Description:</h3>
        <p style={{ width: "50%" }}>{order.description}</p>
        <h3>
          Price: <i>{order.price}</i>
        </h3>
        <div>
          <Formik
            initialValues={{
              id: id,
              title: order.title,
              restaurant: order.restaurant,
              price: order.price,
              description: order.description,
              notes: order.notes,
              src: order.src,
            }}
            onSubmit={async (values) => {
              //   await sleep(500);
              editOrder(values);
              alert(`${order.title} Edited`);
              document.getElementById("submit").disabled = true;
            }}
          >
            {() => (
              <Form>
                <label htmlFor="notes">Notes</label>
                <Field name="notes" placeholder="Notes for Restaurant?" />
                <div></div>
                <button
                  id="submit"
                  className="btn btn-primary btn-xl text-uppercase js-scroll-trigger"
                  type="submit"
                >
                  Edit this Order
                </button>
              </Form>
            )}
          </Formik>
          <Formik
            initialValues={{
              id: id,
              title: order.title,
              restaurant: order.restaurant,
              price: order.price,
              description: order.description,
              notes: order.notes,
            }}
            onSubmit={async (values) => {
              //   await sleep(500);
              deleteOrder(values);
              alert(`${order.title} Removed`);
              document.getElementById("submit").disabled = true;
            }}
          >
            {() => (
              <Form>
                <button
                  id="submit"
                  className="btn btn-primary btn-xl text-uppercase js-scroll-trigger"
                  type="submit"
                  style={{ position: "relative", top: "200px" }}
                >
                  Remove from My Orders
                </button>
              </Form>
            )}
          </Formik>
        </div>
      </div>
    </>
  );
}
